// src/components/Search.js
import React, { useState } from 'react';
import axios from 'axios';
import './Search.css'; // 确保 CSS 文件存在并正确导入

function Search() {
  const [searchTerm, setSearchTerm] = useState('');
  const [fundraisers, setFundraisers] = useState([]);
  const [error, setError] = useState(null);

  const handleSearch = async () => {
    if (!searchTerm) {
      alert('Please enter at least one search criteria');
      return;
    }
    try {
      const response = await axios.get(`/api/search?query=${encodeURIComponent(searchTerm)}`);
      setFundraisers(response.data);
      setError(null);
    } catch (error) {
      setError('Failed to fetch fundraisers');
      setFundraisers([]);
    }
  };

  return (
    <div className="search-page">
      <h2>Search Fundraisers</h2>
      <input
        type="text"
        placeholder="Search..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <div className="search-results">
        {fundraisers.map(fundraiser => (
          <div key={fundraiser.FUNDRAISER_ID} className="search-result">
            <h3>{fundraiser.CAPTION}</h3>
            <p>Organizer: {fundraiser.ORGANIZER}</p>
            <p>Category: {fundraiser.CATEGORY_NAME}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Search;
