// src/Sapp.js 主组件
import React from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';

import FundraiserList from './components/FundraiserList';
import Login from './components/Login';
import Register from './components/Register';
import FundraiserDetail from './components/FundraiserDetail';
import NavigationBar from './components/NavigationBar';

function Sapp() {
  return (
    <Router>
      <div>
        <NavigationBar />
        <Switch>
          
          <Route path="/" exact component={FundraiserList} />
          <Route path="/login" component={Login} />
          <Route path="/register" component={Register} />
          <Route path="/fundraisers/:id" component={FundraiserDetail} />
        </Switch>
      </div>
    </Router>
  );
}

export default Sapp;
