// src/components/Search.js
import React, { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './Search.css'; // 确保 CSS 文件存在并正确导入

function Search() {
    const [organizer, setOrganizer] = useState('');
    const [categoryId, setCategoryId] = useState('');
    const [results, setResults] = useState([]);
  
    const handleSearch = async () => {
      try {
        const response = await axios.get(`/api/search?organizer=${encodeURIComponent(organizer)}&categoryId=${categoryId}`);
        setResults(response.data);
      } catch (error) {
        console.error('Search error:', error);
      }
    };
  
    return (
      <div>
        <input
          type="text"
          value={organizer}
          onChange={(e) => setOrganizer(e.target.value)}
          placeholder="Organizer"
        />
        <input
          type="text"
          value={categoryId}
          onChange={(e) => setCategoryId(e.target.value)}
          placeholder="Category ID"
        />
        <button onClick={handleSearch}>Search</button>
        <button onClick={clearSearch}>Clear</button> {/* 添加的 clear 按钮 */}
        <div>
        {results.map((item) => (
          <div key={item.FUNDRAISER_ID}>
            <Link to={`/fundraisers/${item.FUNDRAISER_ID}`}> {/* 修改为链接 */}
              <h3>{item.CAPTION}</h3>
              <p>Organizer: {item.ORGANIZER}</p>
              <p>Category ID: {item.CATEGORY_ID}</p>
            </Link>
            <button>Donate</button> {/* 添加的捐赠按钮 */}
          </div>
        ))}
      </div>
    </div>
  );
}

export default Search;
